import React, { useContext, useEffect, useRef, useState } from 'react'
import "./Create.css"
import useFetch from '../../hooks/useFetch'
import { useNavigate } from 'react-router-dom'
import { ThemeContext } from '../../contexts/ThemeContext'

function Create() {
  const [baslik, setBaslik] = useState('')
  const [aciklama, setAciklama] = useState('')
  const [hazirlanisi, setHazirlanisi] = useState('')
  const [resim, setResim] = useState('')
  const [url, setUrl] = useState('')
  const [malzeme, setMalzeme] = useState('')
  const [malzemeler, setMalzemeler] = useState([])
  const malzemeInput = useRef(null) /* malzeme inputu üzerinde işlemler yapabilmek için */
  const navigate = useNavigate()
  const {color, mode} = useContext(ThemeContext) 
  const {postData, data} = useFetch('http://localhost:3000/tarifler',"POST")

  const handleSubmit = (e) => {
    e.preventDefault()
    postData({baslik, aciklama, malzemeler, hazirlanisi, resim, url})
  }

  useEffect(() => {
    if(data){
      navigate('/')
    }
  },[data,navigate])

  const handleAddMalzeme = (e) => {
    e.preventDefault()
    const item = malzeme.trim()
    if(item && !malzemeler.includes(item)){
      setMalzemeler(prevItems => [...prevItems, item])
    }
    setMalzeme('')
    malzemeInput.current.value = "" /* ekleme işleminden sonra input içerisini temizler */
    malzemeInput.current.focus() /* ekleme işleminden sonra input içerisinde yazmaya devam edilebilir */
  } 

  return (
    <div className={`text-${mode === "dark" ? "light" : "dark"}`}>
      <h2>Yeni Tarif Ekle</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="baslik" className='form-label'>Başlık</label>
          <input type="text" name="baslik" id="baslik" className='form-control' onChange={(e) => setBaslik(e.target.value)} />
        </div>
        <div className="mb-3">
          <label htmlFor="aciklama" className='form-label'>Açıklama</label>
          <input type="text" name="aciklama" id="aciklama" className='form-control' onChange={(e) => setAciklama(e.target.value)} />
        </div>
        <div className="mb-3">
          <label htmlFor="malzemeler" className='form-label'>Malzemeler 
          <ul>{malzemeler.map(malzeme => <li key={malzeme}>{malzeme}</li>)}</ul></label>            
          <div className="input-group">
            <input type="text" ref={malzemeInput} name='malzeme' className='form-control' onChange={(e) => setMalzeme(e.target.value)}/>
            <button className='btn btn-primary' type='submit' onClick={handleAddMalzeme}>+</button>
          </div>
          {/* ref={malzemeInput} ile inputu ilişkilendirdim */}
        </div>
        <div className="mb-3">
          <label htmlFor="hazirlanisi" className='form-label'>Hazırlanışı</label>
          <textarea name="hazirlanisi" id="hazirlanisi" className='form-control' onChange={(e) => setHazirlanisi(e.target.value)} />
        </div>
        <div className="mb-3">
          <label htmlFor="resim" className='form-label'>Resim</label>
          <input type="text" name="resim" id="resim" className='form-control' onChange={(e) => setResim(e.target.value)} />
        </div>
        <div className="mb-3">
          <label htmlFor="url" className='form-label'>Url</label>
          <input type="text" name="url" id="url" className='form-control' onChange={(e) => setUrl(e.target.value)} />
        </div>
        <button type="submit" className='btn btn-outline-primary'>Kaydet</button>
      </form>
    </div>
  )
}

export default Create
