import React, { useEffect, useState } from 'react'
import "./Details.css"
import { useParams } from 'react-router-dom'

function Details() {
  const { id } = useParams()
  const [tarif, setTarif] = useState(null)

  const url = 'http://localhost:3000/tarifler/' + id
  useEffect(() => {
    fetch(url).then(res => res.json()).then(data => setTarif(data))
  },[url]) /* url değişir ise tekrar servise gider ve ürün getirir */

  return (
    <div className="row mt-3">
      {
        tarif && ( 
        /* tarif bilgisi var ise html ler ekrana gelecek bu kontrolü yapmazsam
        null reference hatası alıyorum */
          <>
            <div className="col-4">
              <img src={`/img/${tarif.resim}`} className="img-fluid rounded" alt={tarif.baslik} />
            </div>
            <div className="col-8">
              <h5>{tarif.baslik}</h5>
              <p>{tarif.aciklama}</p>
              <ul>
                {
                  tarif.malzemeler.map(malzeme => (
                    <li>{malzeme}</li>
                  ))
                }
              </ul>
            </div>
            <div className="col-12 mt-3">
              <p>{tarif.hazirlanisi}</p>
              <a href={tarif.url} className='btn btn-outline-success'>Tarifi İncele</a>
            </div>
          </>
        )
      }
    </div>
  )
}

export default Details
