import React, { useEffect, useState } from 'react'
import "./Home.css"

function Home() {

  const [tarifler, setTarifler] = useState(null)

  useEffect(() => {
    fetch('http://localhost:3000/tarifler')
    .then(res => res.json())
    .then(data => setTarifler(data))
  }, []) /* componentDidMount */

  return (
    <div className="row mt-3">
      {
        tarifler && tarifler.map(tarif => (
          <div className='col-3 mb-3'>
            <div className="card">
              <img src={`img/${tarif.resim}`} class="card-img-top" alt={tarif.baslik} />
              <div class="card-body">
                <h5 class="card-title">{tarif.baslik}</h5>
                <p class="aciklama">{tarif.aciklama}</p>
                <a href={tarif.url} class="btn btn-primary">Tarifi incele...</a>
              </div>
            </div>
          </div>
        ))
      }
    </div>
  )
}

export default Home
