import { useState, useEffect } from "react"

const useFetch = (url) => {
    const [data, setData] = useState(null)
    const [isLoading, setLoading] = useState(false) /* loading icon hide */

    useEffect(() => {
        /* promise yöntemi */
        /* fetch(url).then(res => res.json()).then(data => setData(data)) */

        /* async - await yöntemi */
        const fetchData = async () => {
            setLoading(true) /* loading icon show */
            const res = await fetch(url)
            const data = res.json()
            setLoading(false) /* loading icon hide */
            setData(data)
        }

        fetchData()

    },[url])

    return {data, isLoading}
}

export default useFetch
