import React, { useEffect, useState } from 'react'
import "./Home.css"

function Home() {

  const [tarifler, setTarifler] = useState(null)

  useEffect(() => {
    fetch('http://localhost:3000/tarifler')
    .then(res => res.json())
    .then(data => setTarifler(data))
  }, []) /* componentDidMount */

  return (
    <div className="row mt-3">
      {
        tarifler && tarifler.map(tarif => (
          <div class="card" style="width: 18rem;">
            <img src={`img/${tarif.resim}`} class="card-img-top" alt={tarif.baslik} />
            <div class="card-body">
              <h5 class="card-title">{tarif.baslik}</h5>
              <p class="aciklama">{tarif.aciklama}</p>
              <a href="#" class="btn btn-primary">Go somewhere</a>
            </div>
          </div>
        ))
      }
    </div>
  )
}

export default Home
